                        end if

                        select case (currentOcc_int(l))
                        case (0)
                            ! e_{li,jk}
                            excitInfo = create_excitInfo_li_jk(i, j, k, l)
                            ! 2010
                            pgen = calc_pgen_00xx()

                        case (2)
                            ! e_{ki,jl}
                            excitInfo = create_excitInfo_ki_jl(i, j, k, l)
                            ! 2012
                            pgen = calc_pgen_22xx()

                        case (1)
                            ! choose randomly between:
                            ! e_{li,jk} or e_{ki,jl}
                            ! 2011
                            pgen = calc_pgen_1102()

                            if (genrand_real2_dSFMT() < 0.5_dp) then
                                ! e_{li,jk}
                                excitInfo = create_excitInfo_li_jk(i, j, k, l)
                            else
                                ! e_{ki,jl}
                                excitInfo = create_excitInfo_ki_jl(i, j, k, l)
                            end if

                        end select
                    end select
                case (1)
                    ! n(j) = 1, free k
